CREATE TABLE IF NOT EXISTS public."KMU_MergeHistory"
(
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
    "InhospId" text COLLATE pg_catalog."default",
    chr_halth_id text COLLATE pg_catalog."default",
    mh_health_id text COLLATE pg_catalog."default",
    merged_time timestamp without time zone NOT NULL,
    CONSTRAINT "PK_KMU_MergeHistory" PRIMARY KEY ("Id")
)

TABLESPACE pg_default;

ALTER TABLE IF EXISTS public."KMU_MergeHistory"
    OWNER to postgres;

